DROP PROCEDURE IF EXISTS `addTask`;
DELIMITER $$
CREATE DEFINER=`root`@`localhost` PROCEDURE `addTask`
(
    IN taskTypeId int,
    IN productId int,
    IN envId int,
    IN requesterId int,
    IN priorityId int,
    IN open_date DATE,
    IN statusId int,
    IN qaGO bool,
    IN rollBack bool,
    IN urgent bool,
    IN additionalInfo TEXT
)
BEGIN
     VALUE (taskTypeId,productId,envId,requesterId,priorityId,open_date,NULL,statusId,qaGO,rollBack,urgent,additionalInfo,NULL,NULL);
END;
$$


CREATE OR REPLACE VIEW v_Tasks AS
SELECT t.id, open_date, statusName as status, exec_date, taskType
FROM tasks t
JOIN taskTypes tt on t.taskTypeId = tt.id
JOIN status s on t.statusId = s.id ORDER BY id;

CREATE OR REPLACE VIEW v_Users AS
SELECT u.id,full_name,ut.userType as type,email,password
FROM users u
JOIN userTypes ut on u.type = ut.id
ORDER BY u.id;


